    return "violet"
  }
  return (
    <div className="container">
        <div className='progress-bar'>
            <div className='progress-bar-fill' style={{width: `${progress}`, backgroundColor: getColor()}}>

            </div>
        </div>
        <div className='progress-label'>{}</div>
      </div>
  )
}
