import React, { useState } from 'react';
import "./progressBar.css";
import star from "./star.svg";

export function ProgressBar({ thresholds, value }) {
  const widthOfOneEl = 100 / thresholds.length

  function calcWidth(value, arr) {
    
    const indexNextEl = arr.findIndex((x) => +value <= x)
    if (+value <= arr[0]) {
      return +value / arr[indexNextEl]
    }
    if (+value >= arr[arr.length -1]) {
      return arr.length
    }
    
    const k = (value - arr[indexNextEl - 1])/(arr[indexNextEl] - arr[indexNextEl - 1])
    console.log(k, "?")
    const result = k + indexNextEl
    return result
  }

  const percentColorFill = Math.min(1, calcWidth(value, thresholds) / thresholds.length) * 100

  const k = calcWidth(value, thresholds)

  const [color, setColor] = useState("yellow")

  function getColor() {
    setColor(() => {
      if (value > 0){
        return "yellow"
      }
      else if (value > 50) {
        return "green"
      } else if (value > 200) {
        return "darkgreen"
      } else if (value > 500) {
        return "blue"
      } else {
        return "ligthgreen"
      }
    })
  }
  console.log(getColor(value), "color")
  return (
    <div className="container">
      <div className='progress-bar'>
        <div className='background'
          style={{ '--translate': `${percentColorFill}%` , '--background': `${getColor(value)}`}}
        />
        {thresholds.map((step, i) => {
          return (
            <div className='progress-item'
              style={{
                width: `${widthOfOneEl}%`,
                borderRight: i === thresholds.length - 1 ? 0 : "2px solid #444"
              }}
            >
              <div className='progress-star'>
                {i !== thresholds.length - 1 &&
                  <img
                    src={star}
                    className='progress-star-img'
                    style={{ top: "-10px", left: `78px`, background: i <= k - 1 ? "green" : ""}} />
                }
              </div>
              <div className='progress-step'>
                <span style={{ top: "35px", left: `70px`, display: "inline-block", position: "absolute" }}>{step}</span>
              </div>
            </div>
          )
        })}
      </div>
    </div>
  )
}

