import React from 'react';
import { inflationRates } from './data/inflation';
import { roiYears } from './data/roiYears';

export function Table({
  currentPortfolio,
  annualSavings,
  annualRoiPercentage,
  retirementExpenses,
}) {
  const data = doMath(currentPortfolio, annualSavings, annualRoiPercentage, inflationRates, retirementExpenses);
  
  return (
    <table>
      <tbody>
        <tr>
          <th>Year</th>
          <th>Savings</th>
          <th>ROI</th>
          <th>Net Worth</th>
          <th>Target</th>
        </tr>
        {data.map(({ portfolio, annualSavings, annualRoi, year, retirementExpenses }) => {
          const minRetirementPortfolio = retirementExpenses / (annualRoiPercentage / 100);

          return (
            <tr style={{background: portfolio < minRetirementPortfolio ? "#ffca00" : "#47d982"}}>
              <td>{year}</td>
              <td>{formatNumbers(annualSavings)}</td>
              <td>{formatNumbers(annualRoi)}</td>
              <td>{formatNumbers(portfolio)}</td>
              <td>{formatNumbers(minRetirementPortfolio)}</td>
            </tr>
          )
        })}
      </tbody>
    </table>
  )
}

function doMath(currentPortfolio, annualSavings, annualRoiPercentage, inflationRates, retirementExpenses) {
  const currentYear = 2013//new Date().getFullYear()
  const result = []
  const initialRoi = roiYears[currentYear]
  let portfolio = currentPortfolio;
  for (let i = 0; i < 11; i++) {
    const retirementExpensesInflationAdjusted = retirementExpenses * (1 + inflationRates[currentYear] / 100) ** i;
    const annualRoi = portfolio * (inflationRates[currentYear]) / 100 * roiYears[currentYear] / initialRoi; //portfolio * (inflationRates[currentYear] + annualRoiPercentage) / 100
    console.log(roiYears[currentYear], initialRoi)
    const annualSavingsInflationAdjusted = annualSavings * (1 + inflationRates[currentYear] / 100) ** i;
    portfolio += annualSavingsInflationAdjusted + annualRoi;
    result.push({
      year: currentYear + i,
      annualSavings: annualSavingsInflationAdjusted,
      annualRoi,
      portfolio,
      retirementExpenses: retirementExpensesInflationAdjusted,
    })
  }
  return result
}

function formatNumbers(num) {
  let str = num.toFixed(0)
  const result = []

  while (str.length > 0) {
    result.unshift(str.slice(-3))
    str = str.slice(0, -3)
  }

  return result.join(" ")
}



