export function getData(data, contribution) {
    const result = []
  
    let cumulativeInflation = 1;
    let currentBalance = 0;
  
    result.push( {month: data[0].month, year: data[0].year, currentBalance} )
  
    for (let i = 1; i < data.length; i++) {
      const { month, year } = data[i]
      const monthOnMonthGrowth = data[i].index / data[i - 1].index;
      currentBalance += contribution;
      currentBalance *= monthOnMonthGrowth;
      
      // 100_000 * 1.1 = 110_000
      /* cumulativeInflation *= 1 + inflation[`${month}.${year}`]/100 */
      result.push({
        month,
        year,
        currentBalance: Math.round(currentBalance),
        cumulativeInflation,
        inflationAdjusted: contribution * cumulativeInflation,
      })
    }
    return result
  }
