import './App.css';
import { useState } from 'react';
import { ProgressBar } from './progressBar/ProgressBar';
import { InputData } from './calculator/InputData';
import { Graph } from './calculator/graph/Graph';
import { roiYears } from './calculator/data/roiYears';

const thresholds = [25, 50, 100, 200, 500, 1000]

function App() {
  const [progress, setProgress] = useState(25)

  const handleChange = (e) => {
    setProgress(e.target.value)
  }
  return (
    <div>
      {/* <input type='number' value={progress} onChange={handleChange} style={{marginTop: "30px"}}/>
      <ProgressBar thresholds={thresholds} value={progress}/> */}
      <InputData />
      <Graph roiRates={roiYears}/>
    </div>
  );
}

export default App;
