export function formatPrice(price) {
    return price
    .string()
        .split("")
        .map((num, i) => {
            const iFromEnd = price.length - i;
            if (iFromEnd % 3 === 0) {
                return ' ' + num
            }
            return num
        })
        .join("");
}
