import React, { useState } from 'react'
import { Table } from './Table';

export function InputData() {
  const [currentPortfolio, setCurrentPortfolio] = useState(20_000_000);
  const [annualSavings, setAnnualSavings] = useState(10_000_000);
  const [annualRoiPercentage, setAnnualRoiPercentage] = useState(3)
  const [retirementExpenses, setRetirementExpenses] = useState(1_000_000)

  return (
    <div>
        <label>
          Накопления сейчас
          <input type="text" value={currentPortfolio} onChange={(e) => setCurrentPortfolio(e.target.value)}/>
        </label>
        <label>
          Ежегодные сбережения
          <input type="text" value={annualSavings} onChange={(e) => setAnnualSavings(e.target.value)}/>
        </label>
        <label>
          ROIPercentage
          <input type="text" value={annualRoiPercentage} onChange={(e) => setAnnualRoiPercentage(e.target.value)}/>
        </label>
        <label>
          Траты на пенсии
          <input type="text" value={retirementExpenses} onChange={(e) => setRetirementExpenses(e.target.value)}/>
        </label>
        <Table currentPortfolio={currentPortfolio} annualRoiPercentage={annualRoiPercentage} annualSavings={annualSavings} retirementExpenses={retirementExpenses}/>
    </div>
  )
}
