import React, { useState, useEffect } from 'react';
import { inflation } from './inflation';
import "./moscowExchange.css"

export function MoscowExchange() {
  const [indexDates, setIndexDates] = useState(null);

  useEffect(() => {
    fetch("./indices.csv").then(r => r.text()).then(text => {
      const data = text.split("\n").slice(1).map(line => {
        const [date, index] = line.split(",");
        return { date, index };
      });
      setIndexDates(data);
    });
  }, []);

  const startMonth = 5;
  const startYear = 2014;

  const contribution = 100_000;

  // https://www.youtube.com/watch?v=zJDtL-vfWfY
  

  if (indexDates === null) {
    return null;
  }

  

  console.log(groupData(indexDates, startMonth, startYear), ">")
  const data = groupData(indexDates, startMonth, startYear)

  const result = []
  const startIndex = data[0].index;

  let cumulativeInflation = 1;

  for (const { month, year, index} of data) {
    const currentBalance = contribution * index / startIndex
    cumulativeInflation *= 1 + inflation[`${month}.${year}`]/100
    result.push({
      month,
      year,
      currentBalance,
      cumulativeInflation,
      inflationAdjusted: contribution * cumulativeInflation,
      balance: contribution * cumulativeInflation + 100_000
    })
  }

  console.table(result)
  // for 

  // MM.YY  Balance  M/M  Total
  // 05.21  100_000                
  // 06.21  105_000  +5%    +5%
  // 07.21   95_000  -9.5%  -5%      

  return (
    <table className='table'>
      <thead>
        <tr>
          <th>
            year
          </th>
          <th>
              month
          </th>
          <th>
              current balance
          </th>
          <th>
              cumulative inflation
          </th>
          <th>
              inflation adjusted
          </th>
          <th>
            balance
          </th>
        </tr>
      </thead>
      <tbody>
        {result.map(({year, month, currentBalance, cumulativeInflation, inflationAdjusted, balance}) => {
          return (
            <tr>
              <td>
                {year}
              </td>
              <td>
                {month}
              </td>
              <td>
                {currentBalance.toFixed()}
              </td>
              <td>
                {cumulativeInflation.toFixed(2)}
              </td>
              <td>
                {inflationAdjusted.toFixed(2)}
              </td>
              <td>
                {balance}
              </td>
            </tr>
          )
        })}
      </tbody>
    </table>
  )
}

function groupData(indexDates, startMonth, startYear) {
  const results = []
  while(true) {
    const dateStart = `${startYear}-${startMonth.toString().padStart(2, "0")}`
    const startIndex = indexDates.find(({date}) => date.startsWith(dateStart))
    if (startIndex === undefined) {
      break
    }
    results.push({
      year: startYear,
      month: startMonth,
      index: startIndex.index
    })
    startMonth++
    if (startMonth > 12) {
      startMonth = 1
      startYear++
    }
  }
  return results
  // return [
  //   { month: 5, year: 2021, index },
  //   { month, year, index },
  //   { month, year, index },
  //   { month: 3, year: 2024, index },
  // ]
}
