import React, { useState, useEffect } from 'react';

export function MoscowExchange() {
  const [indexDates, setIndexDates] = useState(null);

  useEffect(() => {
    fetch("./indices.csv").then(r => r.text()).then(text => {
      const data = text.split("\n").slice(1).map(line => {
        const [date, index] = line.split(",");
        return { date, index };
      });
      setIndexDates(data);
    });
  }, []);

  const startMonth = 5;
  const startYear = 2021;

  const contribution = 100_000;

  // https://www.youtube.com/watch?v=zJDtL-vfWfY
  

  if (indexDates === null) {
    return null;
  }

  

  console.log(groupData(indexDates, startMonth, startYear), ">")

  return (
    <table></table>
  )
}

function groupData(indexDates, startMonth, startYear) {
  const results = []
  const dateStart = `${startMonth.toString().padStart(2, "0")}.${startYear}`
  while(true) {
    const startIndex = indexDates.find(({date}) => date.startsWith(dateStart))
    console.log(startIndex)
    if (startIndex === undefined) {
      break
    }
    results.push({
      year: startYear,
      month: startMonth,
      index: startIndex
    })
    startMonth++
    startYear++
  }
  return results
  // return [
  //   { month: 5, year: 2021, index },
  //   { month, year, index },
  //   { month, year, index },
  //   { month: 3, year: 2024, index },
  // ]
}
