import React from 'react'

export function Table({
  currentPortfolio,
  annualSavings,
  annualRoi,
  retirementExpenses,
}) {
  const data = doMath(currentPortfolio, annualSavings, annualRoi);
  console.log(data)
  return (
    <tbody>
        <tr>
            <th>Year</th>
            <th>Savings</th>
            <th>ROI</th>
            <th>Net Worth</th>
        </tr>
        <tr>
            <td>2023</td>
            <td></td>
        </tr>
    </tbody>
  )
}

function doMath(currentPortfolio, annualSavings, annualRoi) {
    const currentYear = new Date().getFullYear()
    const result = []

    for (let i = 0; i < 30; i++) {
        result.push({
          year: currentYear + i,
          savings: annualSavings,
          roi: annualRoi,
          portfolio: currentPortfolio + annualSavings + annualRoi*currentPortfolio/100
      })
    }
    return result
}

console.table(doMath(20_000_000, 10_000_000, 3));

