import React from 'react'

export function Table({
  currentPortfolio,
  annualSavings,
  annualRoiPercentage,
  retirementExpenses,
}) {
  const data = doMath(currentPortfolio, annualSavings, annualRoiPercentage);
  console.log(data)
  return (
    <table>
      <tbody>
        <tr>
          <th>Year</th>
          <th>Savings</th>
          <th>ROI</th>
          <th>Net Worth</th>
        </tr>
        {data.map(({ portfolio, annualSavings, annualRoi, year }) => {
          return (
            <tr>
              <td>{year}</td>
              <td>{annualSavings}</td>
              <td>{annualRoi.toFixed(0)}</td>
              <td>{portfolio.toFixed(0)}</td>
            </tr>
          )
        })}
      </tbody>
    </table>
  )
}

function doMath(currentPortfolio, annualSavings, annualRoiPercentage) {
  const currentYear = new Date().getFullYear()
  const result = []

  let portfolio = currentPortfolio;
  for (let i = 0; i < 30; i++) {
    const annualRoi = portfolio * annualRoiPercentage / 100;
    portfolio += annualSavings + annualRoi;
    result.push({
      year: currentYear + i,
      annualSavings,
      annualRoi,
      portfolio,
    })
  }
  return result
}

function formatNumbers(num) {
    const str = num.toFixed(0)
    let result = []
    
    while(str.length > 0) {
      result.unshift(str.slice(-3))
      str = str.slice(0, -3)
    }
    return result.join(" ")
}

console.log(formatNumbers(12345678.90)) //"12 345 678"

