import React, { useState } from 'react';
import "./progressBar.css"

export function ProgressBar({ thresholds, value }) {
  const [progress, setProgress] = useState(value)
  // [25, 50, 100, 200, 500, 1000]
  const getColor = () => {
    return "violet"
  }
  const widthOfOneEl = 100/thresholds.length
  function calcWidth(value, arr) {
    if (arr.includes(value)) {
        return arr.indexOf(value) + 1
    }
    const indexElAfterValue = arr.findIndex((x) => value < x)
    const nextElAfterValue = arr[indexElAfterValue]
    const prevElementBeforeValue = arr[indexElAfterValue - 1] === undefined ? 0 : arr[indexElAfterValue - 1]
    const stepBetween = nextElAfterValue - prevElementBeforeValue
    const remainder = value/stepBetween > 1 ? value/stepBetween - 1 : value/stepBetween
    const result = remainder + indexElAfterValue
    return result
  }
  console.log(calcWidth(75, thresholds))
  const percentColorFill = calcWidth(value, thresholds)/thresholds.length*100
  const percentColorEmpty = 100 - percentColorFill
  console.log(percentColorFill, percentColorEmpty)
  return (
    <div className="container">
        <div className='progress-bar' 
          style={{background: `linear-gradient(to right, red ${percentColorFill}% ${percentColorEmpty}%, blue ${percentColorFill}% 100%)`}}
        >
            {thresholds.map((step) => {
                return (
                    <div className='progress-step' style={{ width: `${widthOfOneEl}%`}}>
                        âœ©{step}
                    </div>
                )
            })}
        </div>
        <div>{progress}</div>
        <button>progress</button>
      </div>
  )
}

