import React, { useState } from 'react';
import "./progressBar.css"

export function ProgressBar({ thresholds, value }) {
  const [progress, setProgress] = useState(value)
  // [25, 50, 100, 200, 500, 1000]
  const getColor = () => {
    return "violet"
  }
  const widthOfOneEl = 100/thresholds.length
  function calcWidth(value, arr) {
    const indexElAfterValue = arr.findIndex((x) => value < x)
    const nextElAfterValue = arr[indexElAfterValue]
    const prevElementBeforeValue = arr[indexElAfterValue - 1]
    const stepBetween = nextElAfterValue - prevElementBeforeValue
    const deference = nextElAfterValue - value
    const remainder = stepBetween/deference
    return indexElAfterValue + remainder
  }
  console.log(calcWidth(75, thresholds))
  return (
    <div className="container">
        <div className='progress-bar' style={{display: "flex"}}>
            {thresholds.map((step, i) => {
                
                return (
                    <div className='progress-step' style={{background: "grey", height: "30px", width: `${widthOfOneEl}%`}}>âœ©
                    <div className='progress-fill'>{step}</div>
                    </div>
                )
            })}
        </div>
        <div className='progress-label'>{progress}</div>
        <button>progress</button>
      </div>
  )
}

