import React from 'react'

export function Table({
  currentPortfolio,
  annualSavings,
  annualRoiPercentage,
  retirementExpenses,
}) {
  const data = doMath(currentPortfolio, annualSavings, annualRoiPercentage);
  console.log(data)
  return (
    <tbody>
      <tr>
        <th>Year</th>
        <th>Savings</th>
        <th>ROI</th>
        <th>Net Worth</th>
      </tr>
      {data.map(({ portfolio, annualSavings, annualRoi, year }) => {
        return (
          <tr>
            <td>{year}</td>
            <td>{annualSavings}</td>
            <td>{annualRoi.toFixed()}</td>
            <td>{portfolio.toFixed(0)}</td>
          </tr>
        )
      })}
    </tbody>
  )
}

function doMath(currentPortfolio, annualSavings, annualRoiPercentage) {
  const currentYear = new Date().getFullYear()
  const result = []

  let portfolio = currentPortfolio;
  for (let i = 0; i < 30; i++) {
    const annualRoi = portfolio * annualRoiPercentage / 100;
    portfolio += annualSavings + annualRoi;
    result.push({
      year: currentYear + i,
      annualSavings,
      annualRoi,
      portfolio,
    })
  }
  return result
}

