import React, { useState, useEffect } from 'react';
import { inflation } from './inflation';

export function MoscowExchange() {
  const [indexDates, setIndexDates] = useState(null);

  useEffect(() => {
    fetch("./indices.csv").then(r => r.text()).then(text => {
      const data = text.split("\n").slice(1).map(line => {
        const [date, index] = line.split(",");
        return { date, index };
      });
      setIndexDates(data);
    });
  }, []);

  const startMonth = 5;
  const startYear = 2021;

  const contribution = 100_000;

  // https://www.youtube.com/watch?v=zJDtL-vfWfY
  

  if (indexDates === null) {
    return null;
  }

  

  console.log(groupData(indexDates, startMonth, startYear), ">")
  const data = groupData(indexDates, startMonth, startYear)

  const result = []
  const startIndex = data[0].index;

  let cumulativeInflation = +inflation[`${startMonth}.${startYear}`];

  for (const { month, year, index} of data) {
    const currentBalance = contribution * index / startIndex
    cumulativeInflation *= +inflation[`${month}.${year}`]
    result.push({
      month,
      year,
      currentBalance,
      cumulativeInflation,
      inflationAdjusted: contribution * cumulativeInflation/100 //inflation[`${month}.${year}`]
    })
  }

  console.table(result)
  // for 

  // MM.YY  Balance  M/M  Total
  // 05.21  100_000                
  // 06.21  105_000  +5%    +5%
  // 07.21   95_000  -9.5%  -5%      

  return (
    <table>
      <tbody>
        {/* {tableData.map(({year, month, index}) => {
          return (
            <tr>
              <td></td>
            </tr>
          )
        })} */}
      </tbody>
    </table>
  )
}

function groupData(indexDates, startMonth, startYear) {
  const results = []
  while(true) {
    const dateStart = `${startYear}-${startMonth.toString().padStart(2, "0")}`
    const startIndex = indexDates.find(({date}) => date.startsWith(dateStart))
    if (startIndex === undefined) {
      break
    }
    results.push({
      year: startYear,
      month: startMonth,
      index: startIndex.index
    })
    startMonth++
    if (startMonth > 12) {
      startMonth = 1
      startYear++
    }
  }
  return results
  // return [
  //   { month: 5, year: 2021, index },
  //   { month, year, index },
  //   { month, year, index },
  //   { month: 3, year: 2024, index },
  // ]
}
