import React, { useState } from 'react';
import "./progressBar.css";
import star from "./star.svg";

export function ProgressBar({ thresholds, value }) {
  const [progress, setProgress] = useState(value)
  
  const widthOfOneEl = 100 / thresholds.length
  function calcWidth(value, arr) {
    if (arr.includes(value)) {
      return arr.indexOf(value) + 1
    }
    const indexNextEl = arr.findIndex((x) => value < x)
    const prevValue = arr[indexNextEl - 1] === undefined ? arr[indexNextEl] : arr[indexNextEl - 1]
    const k = value / prevValue > 1 ? value / prevValue - 1 : value / prevValue
    const result = k + indexNextEl
    return result
  }
  const percentColorFill = calcWidth(value, thresholds) / thresholds.length * 100
  const k = calcWidth(value, thresholds)
  return (
    <div className="container">
      <div className='progress-bar'
        style={{ background: `linear-gradient(to right, violet ${percentColorFill}%, #eee ${percentColorFill}%)`, transition: `background-color 1s ease-out` }}
      >
        {thresholds.map((step, i) => {
          return (
            <div className='progress-item'
              style={{
                width: `${widthOfOneEl}%`,
                borderRight: i === thresholds.length - 1 ? 0 : "2px solid #444"
              }}
            >
              <div className='progress-star'>
                {i !== thresholds.length - 1 &&
                  <img
                    src={star}
                    className='progress-star-img'
                    style={{ top: "-10px", left: `78px` }} />
                }
              </div>
              <div className='progress-step'>
                <span style={{ top: "35px", left: `70px`, display: "inline-block", position: "absolute" }}>{step}</span>
              </div>
            </div>
          )
        })}
      </div>
    </div>
  )
}

